$(document).ready(function(){var $token_input=$("#token_login_form input[name='fs_ptoken']");$token_input.on("keyup",function(event){var selection=window.getSelection().toString();if(selection!==""){return}if($.inArray(event.keyCode,[38,40,37,39])!==-1){return}var $this=$(this);var input=$this.val();input=input.toUpperCase();input=input.replace(/([^0-9A-Z])/g,"");var split=3;var chunks=[];for(var i=0,len=input.length;i<len;i+=split){chunks.push(input.substr(i,split))}$this.val(function(){return chunks.join(" - ")})})});